	echo '<div class="impact fix">';
	echo '<div class="impact-desc">';
	echo '<h6>Want more information or need advice?</h6><h4>Call: 01344 624480</h4></p>';
	echo '</div></div>';
	echo '<div class="up_login"><button class="btn-home btn  btn-mini " type="button"> <i class="icon-user"></i> &nbsp;Client Login</button> &nbsp;<button class="btn-home btn  btn-mini " type="button"> <i class="icon-group"></i> &nbsp;Advisor Login</button></div>';
	echo '</div></div></div></div>';
}

